query.default:
select
  CurrentNumber
from
  Sungero_DocRegister_CurrentNumbers
where
  DocRegisterId = {0}
  and Month = {1}
  and Year = {2}
  and LeadDocument = {3}
  and Quarter = {4}
  and Department = {5}
  and BUnit = {6}
  and Day = {7}

query.mssql:
if exists (select
             * 
           from
             Sungero_DocRegister_CurrentNumbers
           where
             DocRegisterId = {0}
             and Month = {2}
             and Year = {3}
             and LeadDocument = {4}
             and Quarter = {5}
             and Department = {6}
             and BUnit = {7}
             and Day = {8})
  update
    Sungero_DocRegister_CurrentNumbers
  set
    CurrentNumber = {1}
  where
    DocRegisterId = {0}
    and Month = {2}
    and Year = {3}
    and LeadDocument = {4}
    and Quarter = {5}
    and Department = {6}
    and BUnit = {7}
    and Day = {8}
else
  insert into
    Sungero_DocRegister_CurrentNumbers (DocRegisterId, CurrentNumber, Month, Year, LeadDocument, Quarter, Department, BUnit, Day)
  values
    ({0}, {1}, {2}, {3}, {4}, {5}, {6}, {7}, {8})
