query.mssql:
-- Создать таблицу параметров. if not exists(select 1 from sys.objects where object_id = OBJECT_ID(N'[dbo].[Sungero_Docflow_Params]') and type in (N'U')) create table [Sungero_Docflow_Params] ( [Key] varchar(50), [Value] varchar(max)
constraint PK_Key primary key clustered ([Key]) with (PAD_INDEX = off, STATISTICS_NORECOMPUTE = off, IGNORE_DUP_KEY = off, ALLOW_ROW_LOCKS = on, ALLOW_PAGE_LOCKS = on) on [primary] ) on [primary]
if not exists(select 1 from [dbo].[Sungero_Docflow_Params] where [Key] = 'LastNotificationOfAssignment') insert [Sungero_Docflow_Params] ([Key],Value) values ('LastNotificationOfAssignment', convert(varchar, getutcdate(), 127) + '+0')

query.postgres:
DO $$
BEGIN
  CREATE TABLE IF NOT EXISTS Sungero_Docflow_Params
  (
    Key citext NOT NULL CHECK (char_length(Key::text) <= 50),
    Value citext,
    PRIMARY KEY (Key)
  );

  IF NOT EXISTS(SELECT 1 FROM Sungero_Docflow_Params WHERE Key = 'LastNotificationOfAssignment')
  THEN
    INSERT INTO Sungero_Docflow_Params(Key, Value) VALUES ('LastNotificationOfAssignment', to_char(CURRENT_TIMESTAMP, 'YYYY-MM-DD"T"HH24:MI:SS.MSOF'));
  END IF;
END$$

