query.mssql:
if exists (select * from information_schema.tables where table_name = '{0}') drop table {0}
select r.Storage, rdk.DocumentKind, case when r.DocDateType = 'DocumentDate' then dateadd(DD, -1 * r.DaysToMove, @now) else @maxDate end as DocumentDate, case when r.DocDateType = 'Modified' then dateadd(DD, -1 * r.DaysToMove, @now) else @maxDate end as Modified, case when r.Discriminator = '9fed5653-77e7-4543-b071-6586033907ef' then r.[Priority] else r.[Priority] + (select coalesce(max(r2.[Priority]), 0) from Sungero_Docflow_StoragePolicy r2 where r2.Discriminator = '9fed5653-77e7-4543-b071-6586033907ef') end as [Priority], case when coalesce(r.NextRetention, @now) <= @now then 1 else 0 end as NeedRetention into {0} from Sungero_Docflow_StoragePolicy r join Sungero_Docflow_SPolicyDocKind rdk on rdk.StoragePolicy = r.Id where r.Status = 'Active';
insert into {0} select r.Storage, rdk.Id, case when r.DocDateType = 'DocumentDate' then dateadd(DD, -1 * r.DaysToMove, @now) else @maxDate end as DocumentDate, case when r.DocDateType = 'Modified' then dateadd(DD, -1 * r.DaysToMove, @now) else @maxDate end as Modified, case when r.Discriminator = '9fed5653-77e7-4543-b071-6586033907ef' then r.[Priority] else r.[Priority] + (select coalesce(max(r2.[Priority]), 0) from Sungero_Docflow_StoragePolicy r2 where r2.Discriminator = '9fed5653-77e7-4543-b071-6586033907ef') end as [Priority], case when coalesce(r.NextRetention, @now) <= @now then 1 else 0 end as NeedRetention from Sungero_Docflow_StoragePolicy r join Sungero_Docflow_DocumentKind rdk on 1=1 where r.Status = 'Active' and not exists (select 1 from Sungero_Docflow_SPolicyDocKind k where k.StoragePolicy = r.Id);
insert into {0} select s.Id, rdk.Id, @maxDate, @maxDate, -1, 1 from Sungero_Core_Storage s join Sungero_Docflow_DocumentKind rdk on 1=1 where s.IsDefault = 1;

query.postgres:
DO $$ BEGIN IF EXISTS (SELECT * FROM information_schema.tables WHERE table_name = LOWER('{0}')) THEN DROP TABLE {0}; END IF; END $$;
SELECT r.Storage, rdk.DocumentKind, CASE WHEN r.DocDateType = 'DocumentDate' THEN @now - r.DaysToMove * INTERVAL '1 day' ELSE @maxDate END AS DocumentDate, CASE WHEN r.DocDateType = 'Modified' THEN @now - r.DaysToMove * INTERVAL '1 day' ELSE @maxDate END AS Modified, CASE WHEN r.Discriminator = '9fed5653-77e7-4543-b071-6586033907ef' THEN r.Priority ELSE r.Priority + (SELECT COALESCE(MAX(r2.Priority), 0) FROM Sungero_Docflow_StoragePolicy r2 WHERE r2.Discriminator = '9fed5653-77e7-4543-b071-6586033907ef') END AS Priority, CASE WHEN COALESCE(r.NextRetention, @now) <= @now THEN 1 ELSE 0 END AS NeedRetention INTO {0} FROM Sungero_Docflow_StoragePolicy r JOIN Sungero_Docflow_SPolicyDocKind rdk ON rdk.StoragePolicy = r.Id WHERE r.Status = 'Active';
INSERT INTO {0} SELECT r.Storage, rdk.Id, CASE WHEN r.DocDateType = 'DocumentDate' THEN @now - r.DaysToMove * INTERVAL '1 day' ELSE @maxDate END AS DocumentDate, CASE WHEN r.DocDateType = 'Modified' THEN @now - r.DaysToMove * INTERVAL '1 day' ELSE @maxDate END AS Modified, CASE WHEN r.Discriminator = '9fed5653-77e7-4543-b071-6586033907ef' THEN r.Priority ELSE r.Priority + (SELECT COALESCE(MAX(r2.Priority), 0) FROM Sungero_Docflow_StoragePolicy r2 WHERE r2.Discriminator = '9fed5653-77e7-4543-b071-6586033907ef') END AS Priority, CASE WHEN COALESCE(r.NextRetention, @now) <= @now THEN 1 ELSE 0 END AS NeedRetention FROM Sungero_Docflow_StoragePolicy r JOIN Sungero_Docflow_DocumentKind rdk ON 1=1 WHERE r.Status = 'Active' AND NOT EXISTS (SELECT 1 FROM Sungero_Docflow_SPolicyDocKind k WHERE k.StoragePolicy = r.Id);
INSERT INTO {0} SELECT s.Id, rdk.Id, @maxDate, @maxDate, -1, 1 FROM Sungero_Core_Storage s JOIN Sungero_Docflow_DocumentKind rdk ON 1=1 WHERE s.IsDefault = TRUE;

DO $$
declare documentKindTypeGuid uuid;
declare accessRightTypeSid uuid;
declare documentKindId int;
declare recipientId int;
declare operations int; 
declare secureObjectid int;
declare accessRightsTypeId int;
declare accessRightsId int;
declare accessctrlentNewId int;
declare accessrightentNewId int;

begin
	documentKindTypeGuid := '14a59623-89a2-4ea8-b6e9-2ad4365f358c';
  accessRightTypeSid := 'f3c5d7c0-c5c3-4957-add6-193a2f44f267';
	recipientId := (select r.id from sungero_core_recipient r where r.sid = '440103EA-A766-47A8-98AD-5260CA32DE46');
  operations := 16777216;
  accessctrlentNewId := {0};
  accessrightentNewId := {1};

  accessRightsTypeId := (select art.id from sungero_core_accessrighttyp art where art.sid = accessRightTypeSid);
  
  if (accessRightsTypeId is not null) then 
    -- Выдача экземплярных прав в sungero_system_accessctrlent на каждый вид документа.
    for documentKindId in select id from sungero_docflow_documentkind dk
      where not exists(select 1 from sungero_system_accessctrlent ace where ace.granted = true and 
                    ace.operationset = operations and
                    ace.recipient = recipientid and
                    ace.secureobject = (select so.id from sungero_system_secureobject so where so.entityid = dk.id and so.entitytypeguid = dk.discriminator))
    loop
      secureObjectId := (select so.id from sungero_system_secureobject so where so.entityid = documentKindId and so.entitytypeguid = documentKindTypeGuid);
      if (secureObjectId is not null)
      then
        insert into sungero_system_accessctrlent(id, granted, operationset, recipient, secureobject)
        values (accessctrlentNewId, true, operations, recipientId, secureObjectId);
        accessctrlentNewId := accessctrlentNewId + 1;
      end if;
    end loop;
    
    -- Выдача экземплярных прав в sungero_core_accessrightent на каждый вид документа.
    for documentKindId in select id from sungero_docflow_documentkind dk
      where not exists(select 1 from sungero_core_accessrightent aren where 
                        aren.accessrights = (select ar.id from sungero_core_accessrights ar where  ar.entityid = dk.id and ar.entitytypeguid = dk.discriminator) and 
                   			aren.recipient = recipientid and
                   			aren.accessrightstype = accessRightsTypeId)
    loop
      accessRightsId = (select ar.id from sungero_core_accessrights ar where ar.entityid = documentKindId and ar.entitytypeguid = documentKindTypeGuid);
      if (accessRightsId is not null)
      then
        insert into sungero_core_accessrightent (id, accessrights, recipient, accessrightstype)
        values (accessrightentNewId, accessRightsId, recipientId, accessRightsTypeId);
        accessrightentNewId := accessrightentNewId + 1;
      end if;
    end loop;
  end if;
end $$;

